import org.junit.Assert;
import org.junit.Test;

import static org.junit.Assert.*;

public class DnaRsaTranscriberTest {
    @Test
    public void transcribeSmall() {
        Assert.assertEquals("GAUGGAACUUGACUACGUAAAUU", DnaRsaTranscriber.transcribe("GATGGAACTTGACTACGTAAATT"));
    }

    @Test
    public void transcribeLarge() {
        Assert.assertEquals("",
            DnaRsaTranscriber.transcribe(""));
    }
}
