import org.junit.Assert;
import org.junit.Test;

public class DnaStrandComplementTest {

    @Test
    public void smallDna() {
        Assert.assertEquals("ACCGGGTTTT", DnaStrandComplement.reverseComplement("AAAACCCGGT"));
    }

    @Test
    public void largeDna() {
        Assert.assertEquals("",
            DnaStrandComplement.reverseComplement(""));
    }

}